        <!-- Items -->
        <div class="items-section">
          <div class="section-title">Itens do OrÃ§amento</div>
          <table class="items-table">
            <thead>
              <tr>
                <th>Item</th>
                <th class="text-right">PreÃ§o Unit.</th>
                <th class="text-right">NecessÃ¡rio</th>
                <th class="text-right">JÃ¡ Tenho</th>
                <th class="text-right">Comprar</th>
                <th class="text-right">A Pagar</th>
              </tr>
            </thead>
            <tbody>
              ${quote.items.map(item => {
                const economia = (item.owned_quantity || 0) * (item.unit_price || 0);
                return `
                <tr>
