import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Building, Upload, Save, FileText } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Company } from "@/entities/Company";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCompanySchema } from "@shared/schema";
import { z } from "zod";
import { Link } from "wouter";

const companySchema = insertCompanySchema.extend({
  name: z.string()
    .min(3, "Nome da empresa deve ter pelo menos 3 caracteres")
    .max(100, "Nome da empresa deve ter no mÃ¡ximo 100 caracteres")
    .regex(/^[A-Za-zÃ€-Ã¿0-9\s\-\.&]+$/, "Nome deve conter apenas letras, nÃºmeros e caracteres bÃ¡sicos"),
  logoUrl: z.string().url("URL invÃ¡lida").optional().or(z.literal("")),
});

type CompanyForm = z.infer<typeof companySchema>;

export default function CompanyPage() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: company, isLoading: companyLoading, error: companyError } = useQuery({
    queryKey: ["companies", "me"],
    queryFn: () => Company.me(),
    retry: false,
    enabled: isAuthenticated,
  });

  // Handle company error
  useEffect(() => {
    if (companyError && isUnauthorizedError(companyError as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [companyError, toast]);

  const form = useForm<CompanyForm>({
    resolver: zodResolver(companySchema),
    defaultValues: {
      name: "",
      logoUrl: "",
      subscriptionStatus: "active",
    },
  });

  // Update form when company data loads
  useEffect(() => {
    if (company) {
      form.setValue("name", company.name);
      form.setValue("logoUrl", company.logoUrl || "");
    }
  }, [company, form]);

  const createMutation = useMutation({
    mutationFn: (data: CompanyForm) => Company.create(data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["companies", "me"] });
      toast({
        title: "Sucesso",
        description: "Empresa criada com sucesso!",
      });
      setIsEditing(false);
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao criar empresa. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: (data: CompanyForm) => Company.update(company!.id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["companies", "me"] });
      toast({
        title: "Sucesso",
        description: "Empresa atualizada com sucesso!",
      });
      setIsEditing(false);
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao atualizar empresa. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: CompanyForm) => {
    if (company) {
      updateMutation.mutate(data);
    } else {
      createMutation.mutate(data);
    }
  };

  const handleCancel = () => {
    if (company) {
      form.setValue("name", company.name);
      form.setValue("logoUrl", company.logoUrl || "");
    } else {
      form.reset();
    }
    setIsEditing(false);
  };

  if (isLoading || companyLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    );
  }

  return (
