                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Company Form */}
        <Card className="shadow-lg">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span>{company ? "InformaÃ§Ãµes da Empresa" : "Criar Empresa"}</span>
              {company && !isEditing && (
                <Button 
                  onClick={() => setIsEditing(true)}
                  data-testid="button-edit-company"
                >
                  Editar InformaÃ§Ãµes
                </Button>
              )}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {!company || isEditing ? (
              <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
                {/* Company Logo */}
                <div className="space-y-4">
                  <Label htmlFor="logoUrl">Logo da Empresa</Label>
                  <div className="flex items-center gap-4">
                    {(company?.logoUrl || form.watch("logoUrl")) && (
                      <div className="w-16 h-16 bg-muted rounded-lg flex items-center justify-center border">
                        <img 
                          src={company?.logoUrl || form.watch("logoUrl")} 
                          alt="Logo da empresa"
                          className="w-14 h-14 object-contain rounded"
                          onError={(e) => {
                            (e.target as HTMLImageElement).style.display = 'none';
                          }}
                          data-testid="img-company-logo-preview"
                        />
                      </div>
                    )}
                    <div className="flex-1">
                      <Input
                        id="logoUrl"
                        placeholder="URL do logo da empresa (opcional)"
                        {...form.register("logoUrl")}
                        data-testid="input-logo-url"
                      />
                      {form.formState.errors.logoUrl && (
                        <p className="text-sm text-destructive mt-1">
                          {form.formState.errors.logoUrl.message}
                        </p>
                      )}
                      <p className="text-sm text-muted-foreground mt-1">
                        ForneÃ§a uma URL pÃºblica da imagem do logo (formato: JPG, PNG, SVG)
                      </p>
                    </div>
                  </div>
                </div>

                {/* Company Name */}
                <div className="space-y-2">
