                  >
                    <Save className="w-4 h-4" />
                    {(createMutation.isPending || updateMutation.isPending) 
                      ? "Salvando..." 
                      : company 
                        ? "Salvar AlteraÃ§Ãµes" 
                        : "âœ“ Completar ConfiguraÃ§Ã£o ObrigatÃ³ria"
                    }
                  </Button>
                </div>
              </form>
            ) : (
              /* Display Mode */
              <div className="space-y-6">
                {/* Company Info Display */}
