import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  FileText, 
  DollarSign, 
  Clock, 
  TrendingUp,
  Plus,
  Building,
  BarChart3,
  CreditCard,
  Crown,
  LogOut
} from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { User } from "@/entities/User";
import { Company } from "@/entities/Company";
import { QuoteDraft } from "@/entities/QuoteDraft";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Link } from "wouter";
import type { User as UserType } from "@shared/schema";

export default function Dashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: user } = useQuery<UserType>({
    queryKey: ["/api/auth/user"],
    retry: false,
    enabled: isAuthenticated,
  });

  const { data: company } = useQuery({
    queryKey: ["companies", "me"],
    queryFn: () => Company.me(),
    retry: false,
    enabled: isAuthenticated,
  });

  const { data: quotes = [], error: quotesError } = useQuery({
    queryKey: ["quotes"],
    queryFn: () => QuoteDraft.list(),
    retry: false,
    enabled: isAuthenticated,
  });

  // Handle quotes error
  useEffect(() => {
    if (quotesError && isUnauthorizedError(quotesError as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [quotesError, toast]);

  // Force company setup if not completed
  useEffect(() => {
    if (user && isAuthenticated && !company && !isLoading) {
      // Check if email is missing
      if (!user.email) {
        toast({
          title: "Email ObrigatÃ³rio",
          description: "Um email vÃ¡lido Ã© necessÃ¡rio para usar a plataforma. Entre em contato com o suporte.",
          variant: "destructive",
        });
        return;
      }

      // Redirect to company setup if company is not configured
      setTimeout(() => {
        toast({
          title: "Configure sua Empresa",
          description: "Complete a configuraÃ§Ã£o da sua empresa para continuar usando a plataforma.",
          variant: "default",
        });
        window.location.href = "/company";
      }, 2000);
    }
  }, [user, company, isAuthenticated, isLoading, toast]);

  if (isLoading || !user) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    );
  }

  // Calculate stats
  const stats = {
    totalQuotes: quotes.length,
    totalRevenue: quotes
      .filter(q => q.status === 'finalized')
      .reduce((sum, q) => sum + parseFloat(q.total || '0'), 0),
    pendingQuotes: quotes.filter(q => q.status === 'draft').length,
    conversionRate: quotes.length > 0 
      ? Math.round((quotes.filter(q => q.status === 'finalized').length / quotes.length) * 100)
      : 0
  };

  const recentQuotes = quotes.slice(0, 3);

  const getStatusVariant = (status: string) => {
    switch (status) {
      case 'finalized': return 'default';
      case 'draft': return 'secondary';
      default: return 'outline';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'finalized': return 'text-green-700 bg-green-50 border-green-200';
      case 'draft': return 'text-orange-700 bg-orange-50 border-orange-200';
      default: return 'text-blue-700 bg-blue-50 border-blue-200';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
