              data-testid="button-save-quote"
            >
              <Save className="w-4 h-4 mr-2" />
              {saveMutation.isPending ? "Salvando..." : "Salvar"}
            </Button>
          </div>
        </div>

        {/* Quote Header */}
        <QuoteHeader 
          quote={quoteData} 
          onChange={setQuoteData}
        />

        {/* Template Selector */}
        <TemplateSelector 
          selectedVariant={quoteData.template_variant} 
          onChange={(variant) => setQuoteData(prev => ({ ...prev, template_variant: variant }))}
        />

        {/* Items List */}
        <ItemsList 
          items={quoteData.items} 
          onChange={(items) => setQuoteData(prev => ({ ...prev, items }))}
        />

        {/* Quote Summary and Notes */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <QuoteSummary 
            items={quoteData.items}
            discount={quoteData.discount}
            onDiscountChange={(discount) => setQuoteData(prev => ({ ...prev, discount }))}
          />

          {/* Notes Section */}
          <Card className="border-0 shadow-lg">
            <CardHeader className="border-b border-gray-100">
              <CardTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5 text-blue-600" />
                ObservaÃ§Ãµes
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-6">
              <div className="space-y-2">
                <Label htmlFor="note">ObservaÃ§Ãµes Adicionais</Label>
                <Textarea
                  id="note"
                  value={quoteData.note}
                  onChange={(e) => setQuoteData(prev => ({ ...prev, note: e.target.value }))}
                  placeholder="Adicione observaÃ§Ãµes, termos ou condiÃ§Ãµes especiais..."
                  rows={8}
                  className="resize-none"
                  data-testid="textarea-quote-note"
                />
                <p className="text-xs text-gray-500">
                  As observaÃ§Ãµes aparecerÃ£o no PDF final do orÃ§amento.
                </p>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Bottom Actions */}
        <div className="flex justify-end gap-3 pt-6 border-t">
          <Link href="/quotes">
            <Button variant="outline" data-testid="button-cancel-edit">
              Cancelar
            </Button>
          </Link>
          <Button 
            onClick={handleSave}
            disabled={saveMutation.isPending}
            data-testid="button-save-final"
          >
            <Save className="w-4 h-4 mr-2" />
            {saveMutation.isPending ? "Salvando..." : "Salvar OrÃ§amento"}
          </Button>
        </div>
      </div>
    </div>
  );
}

