import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  FileText, 
  Plus, 
  Edit,
  Trash2,
  Eye,
  Download,
  Search
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { QuoteDraft } from "@/entities/QuoteDraft";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertQuoteDraftSchema } from "@shared/schema";
import { z } from "zod";
import { Link } from "wouter";

const createQuoteSchema = insertQuoteDraftSchema.extend({
  title: z.string().min(1, "TÃ­tulo Ã© obrigatÃ³rio"),
  clientName: z.string().optional(),
  clientEmail: z.string().email("Email invÃ¡lido").optional().or(z.literal("")),
});

type CreateQuoteForm = z.infer<typeof createQuoteSchema>;

export default function Quotes() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: quotes = [], isLoading: quotesLoading, error: quotesError } = useQuery({
    queryKey: ["quotes"],
    queryFn: () => QuoteDraft.list(),
    retry: false,
    enabled: isAuthenticated,
  });

  // Handle quotes error
  useEffect(() => {
    if (quotesError && isUnauthorizedError(quotesError as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [quotesError, toast]);

  const form = useForm<CreateQuoteForm>({
    resolver: zodResolver(createQuoteSchema),
    defaultValues: {
      title: "",
      clientName: "",
      clientEmail: "",
      templateVariant: "variant_a",
      currency: "BRL",
      status: "draft",
    },
  });

  const createMutation = useMutation({
    mutationFn: (data: Omit<CreateQuoteForm, 'code' | 'companyId' | 'createdBy'>) => QuoteDraft.create(data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["quotes"] });
      toast({
        title: "Sucesso",
        description: "OrÃ§amento criado com sucesso!",
      });
      setIsCreateModalOpen(false);
      form.reset();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao criar orÃ§amento. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (quoteId: string) => QuoteDraft.delete(quoteId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["quotes"] });
      toast({
        title: "Sucesso",
        description: "OrÃ§amento excluÃ­do com sucesso!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao excluir orÃ§amento. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleCreateSubmit = (data: CreateQuoteForm) => {
    createMutation.mutate(data);
  };

  const handleDelete = (quoteId: string) => {
    if (confirm("Tem certeza que deseja excluir este orÃ§amento?")) {
      deleteMutation.mutate(quoteId);
    }
  };

  // Filter quotes
  const filteredQuotes = quotes.filter(quote => {
    const matchesSearch = quote.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (quote.clientName && quote.clientName.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = filterStatus === "all" || quote.status === filterStatus;
    return matchesSearch && matchesStatus;
  });

  const getStatusVariant = (status: string) => {
    switch (status) {
      case 'finalized': return 'default';
      case 'draft': return 'secondary';
      default: return 'outline';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'finalized': return 'Finalizado';
      case 'draft': return 'Rascunho';
      default: return status;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-foreground" data-testid="heading-quotes">OrÃ§amentos</h1>
            <p className="text-muted-foreground">Gerencie todos os seus orÃ§amentos</p>
          </div>
          <div className="flex items-center gap-4">
            <Link href="/">
              <Button variant="outline" data-testid="button-back-dashboard">Dashboard</Button>
            </Link>
            <div className="flex items-center gap-2">
              <Link href="/quote-editor">
                <Button data-testid="button-new-advanced-quote">
                  <Plus className="w-4 h-4 mr-2" />
                  Editor AvanÃ§ado
                </Button>
              </Link>
              <Dialog open={isCreateModalOpen} onOpenChange={setIsCreateModalOpen}>
                <DialogTrigger asChild>
                  <Button variant="outline" data-testid="button-new-quote">
                    <Plus className="w-4 h-4 mr-2" />
                    CriaÃ§Ã£o RÃ¡pida
                  </Button>
                </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle data-testid="dialog-title-create-quote">Criar Novo OrÃ§amento</DialogTitle>
                  <DialogDescription>
                    Preencha os dados bÃ¡sicos para criar um novo orÃ§amento
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={form.handleSubmit(handleCreateSubmit)} className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="title">TÃ­tulo do OrÃ§amento *</Label>
                      <Input
                        id="title"
                        placeholder="Ex: Sistema de SeguranÃ§a"
                        {...form.register("title")}
                        data-testid="input-quote-title"
                      />
                      {form.formState.errors.title && (
                        <p className="text-sm text-destructive mt-1">
                          {form.formState.errors.title.message}
                        </p>
                      )}
                    </div>
                    <div>
                      <Label htmlFor="clientName">Nome do Cliente</Label>
                      <Input
                        id="clientName"
                        placeholder="Nome do cliente"
                        {...form.register("clientName")}
                        data-testid="input-client-name"
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="clientEmail">Email do Cliente</Label>
                      <Input
                        id="clientEmail"
                        type="email"
                        placeholder="cliente@empresa.com"
                        {...form.register("clientEmail")}
                        data-testid="input-client-email"
                      />
                      {form.formState.errors.clientEmail && (
                        <p className="text-sm text-destructive mt-1">
                          {form.formState.errors.clientEmail.message}
                        </p>
                      )}
                    </div>
                    <div>
                      <Label htmlFor="templateVariant">Template</Label>
                      <Select
                        value={form.watch("templateVariant") || ""}
                        onValueChange={(value) => form.setValue("templateVariant", value || "variant_a")}
                      >
                        <SelectTrigger data-testid="select-template-variant">
                          <SelectValue placeholder="Selecione um template" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="variant_a">Template A - ClÃ¡ssico</SelectItem>
                          <SelectItem value="variant_b">Template B - Moderno</SelectItem>
                          <SelectItem value="variant_c">Template C - Minimalista</SelectItem>
                          <SelectItem value="variant_d">Template D - Corporativo</SelectItem>
                          <SelectItem value="variant_e">Template E - Elegante</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="note">ObservaÃ§Ãµes</Label>
                    <Textarea
                      id="note"
                      placeholder="ObservaÃ§Ãµes adicionais..."
                      rows={3}
                      {...form.register("note")}
                      data-testid="textarea-quote-note"
                    />
                  </div>

                  <DialogFooter>
                    <Button type="button" variant="outline" onClick={() => setIsCreateModalOpen(false)} data-testid="button-cancel-create">
                      Cancelar
                    </Button>
                    <Button type="submit" disabled={createMutation.isPending} data-testid="button-submit-create">
                      {createMutation.isPending ? "Criando..." : "Criar OrÃ§amento"}
                    </Button>
                  </DialogFooter>
                </form>
              </DialogContent>
            </Dialog>
            </div>
          </div>
        </div>

        {/* Filters */}
        <Card>
          <CardContent className="p-6">
            <div className="flex flex-col md:flex-row gap-4">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Buscar por tÃ­tulo ou cliente..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-quotes"
                  />
                </div>
              </div>
              <Select value={filterStatus} onValueChange={setFilterStatus}>
                <SelectTrigger className="w-full md:w-48" data-testid="select-filter-status">
                  <SelectValue placeholder="Filtrar por status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos os status</SelectItem>
                  <SelectItem value="draft">Rascunho</SelectItem>
                  <SelectItem value="finalized">Finalizado</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </CardContent>
        </Card>

        {/* Quotes Grid */}
        {quotesLoading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary" />
          </div>
        ) : filteredQuotes.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <FileText className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-xl font-semibold text-foreground mb-2" data-testid="heading-no-quotes">
                {quotes.length === 0 ? "Nenhum orÃ§amento encontrado" : "Nenhum resultado encontrado"}
              </h3>
              <p className="text-muted-foreground mb-6">
                {quotes.length === 0 
                  ? "VocÃª ainda nÃ£o criou nenhum orÃ§amento. Que tal comeÃ§ar criando seu primeiro?"
                  : "Tente ajustar os filtros ou termo de busca."
                }
              </p>
              {quotes.length === 0 && (
                <Button onClick={() => setIsCreateModalOpen(true)} data-testid="button-create-first-quote">
                  <Plus className="w-4 h-4 mr-2" />
                  Criar Primeiro OrÃ§amento
